const hashtags = [];
const profiles = [];

const numPosts = ($items("Facebook")[0]?.json?.numPosts) ?? null;

// build profiles + hashtags list
for (const { json } of items) {
  const type   = (json.Type || "").toLowerCase();
  const handle = (json["Hashtag/ Username"] || "").trim();
  if (!handle) continue;

  if (type.includes("profile")) {
    const url = /^https?:\/\//i.test(handle)
      ? handle
      : `https://www.facebook.com/${handle.replace(/^@?\/?/, "")}`;
    profiles.push({ url, method: "GET" });
  } else if (type.includes("hashtag")) {
    hashtags.push(handle);
  }
}

return [
  { json: { hashtags, profiles, numPosts } }
];

const hashtags = [];
const profiles = [];

// get numPosts straight from the LinkedIn If node (first item)
const numPosts = ($items("LinkedIn")[0]?.json?.numPosts) ?? null;

// loop through Airtable search results
for (const { json } of $items("Search LinkedIn Inputs1")) {
  const type   = (json.Type || "").toLowerCase();
  const handle = json["Hashtag/ Username"];
  if (!handle) continue;

  if (type.includes("profile"))
    profiles.push(`https://linkedin.com/in/${handle}`);
  else if (type.includes("company"))
    profiles.push(`https://www.linkedin.com/company/${handle}`);
  else if (type.includes("hashtag"))
    hashtags.push(handle);
}

return [{
  json: { hashtags, profiles, numPosts }
}];

