const boundary = 'divider';
const docName = $input.first().json.document_name;
const folderId = $input.first().json.drive_folder_id;
const htmlContent = $input.first().json.html_content;

const metadata = JSON.stringify({
  name: docName,
  mimeType: "application/vnd.google-apps.document",
  parents: [folderId]
});

const htmlWithStyles = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <style>
    /* Add bottom margin to block elements for spacing */
    p,
    ul,
    ol,
    table,
